public class JavaFlow15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int y=1;
		do{ 
		int x=1;
		    do{
		    System.out.print(y+"*"+x+"="+(x*y)+"\t");
		    x++; 
		      }while(x<=9);
		       System.out.println();
		       y++;
		      }while(y<=9); 
		    }  
		}


