package tw.luchienlin.javavariabledatatype;

public class JavaVariableDataType06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch1 = '\"'; //印出
		char ch2 = '\74'; //印出8進位
		char ch3 = '\u003e'; //印出16進位
		
		System.out.println(ch1+"Time flies."+ch1);
		System.out.println("\"Time is money !\"");
		System.out.println(ch2+"Tomorrow never come"+ch3);
	}

}
